<?phprequire_once('Connections/User_Information.php');if (!function_exists("GetSQLValueString")) {function GetSQLValueString($theValue, $theType, $theDefinedValue = "", $theNotDefinedValue = "") {  if (PHP_VERSION < 6) {    $theValue = get_magic_quotes_gpc() ? stripslashes($theValue) : $theValue;  }  $theValue = function_exists("mysql_real_escape_string") ? mysql_real_escape_string($theValue) : mysql_escape_string($theValue);  switch ($theType) {    case "text":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;        case "long":    case "int":      $theValue = ($theValue != "") ? intval($theValue) : "NULL";      break;    case "double":      $theValue = ($theValue != "") ? doubleval($theValue) : "NULL";      break;    case "date":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;    case "defined":      $theValue = ($theValue != "") ? $theDefinedValue : $theNotDefinedValue;      break;  }  return $theValue;}}//-- USER RECORDSET--mysql_select_db($database_User_Information, $User_Information);$query_user_info = "SELECT * FROM user_table";$user_info = mysql_query($query_user_info, $User_Information) or die(mysql_error());$row_user_info = mysql_fetch_assoc($user_info);$totalRows_user_info = mysql_num_rows($user_info);$row_item_info = mysql_fetch_assoc($user_info);$totalRows_user_info = mysql_num_rows($user_info);//-- ITEM RECORDSET-->mysql_select_db($database_User_Information, $User_Information);$query_item_info = "SELECT brand_name, item_name, upc, nf_calories, nf_total_fat, nf_total_carbohydrate, nf_protein, images_front_full_url FROM fridge_table";$item_info = mysql_query($query_item_info, $User_Information) or die(mysql_error());$row_item_info = mysql_fetch_assoc($item_info);$totalRows_item_info = mysql_num_rows($item_info);?>