<?phpsession_start();if(isset($_POST['action'])){	if($_POST['action']=="setSession")		setSession($_POST['id_number']);	else if($_POST['action']=="destroy")		session_destroy();	else if($_POST['action']=="decrement")		decrement($_POST['SID'],$_POST['Quant_curr']);}function setSession($id_number){	$_SESSION['id_number'] = $id_number;}function decrement($SID,$Quant_curr){ 	/* Attempt MySQL server connection. Assuming you are running MySQL	server with default setting (user 'root' with no password) */	$link = mysqli_connect("localhost", "root", "password", "ltr_fridgedb"); 	// Check connection	if($link === false){    die("ERROR: Could not connect. " . mysqli_connect_error());	}	mysql_select_db($database_User_Information, $User_Information);	if($Quant_curr<=0){		$sql = "DELETE FROM `Inventory` WHERE `SID` =' " .$SID." ' "; 	}	else{		$sql = "UPDATE Inventory ". "SET Quant_curr =' ".$Quant_curr ." ' WHERE SID =' " .$SID." '";	}	if(mysqli_query($link, $sql)){    echo "Records added successfully." ;	} 	else{    echo "ERROR: Could not able to execute $sql. " . mysqli_error($link);} 	}?>